import MockProps from '../Prop/MockProps.mjs'

export default {
  ports: [
    {
      port: 'HDMI1',
      connected: true,
      signal: 'stable',
      arcCapable: true,
      arcConnected: true,
      edidVersion: '2.0',
      autoLowLatencyModeCapable: true,
      autoLowLatencyModeSignalled: true,
    },
  ],
  port: {
    port: 'HDMI1',
    connected: true,
    signal: 'stable',
    arcCapable: true,
    arcConnected: true,
    edidVersion: '2.0',
    autoLowLatencyModeCapable: true,
    autoLowLatencyModeSignalled: true,
  },
  open: null,
  close: null,
  lowLatencyMode: function (params) {
    return MockProps.mock(
      'HDMIInput',
      'lowLatencyMode',
      params,
      undefined,
      0,
      true,
    )
  },
  autoLowLatencyModeCapable: function (params) {
    return MockProps.mock(
      'HDMIInput',
      'autoLowLatencyModeCapable',
      params,
      undefined,
      1,
      true,
    )
  },
  edidVersion: function (params) {
    return MockProps.mock(
      'HDMIInput',
      'edidVersion',
      params,
      undefined,
      1,
      '2.0',
    )
  },
  setLowLatencyMode: function (params) {
    const callbackOrValue = params.value
    delete params.value
    return MockProps.mock(
      'HDMIInput',
      'lowLatencyMode',
      params,
      callbackOrValue,
      0,
      null,
    )
  },
  setAutoLowLatencyModeCapable: function (params) {
    const callbackOrValue = params.value
    delete params.value
    return MockProps.mock(
      'HDMIInput',
      'autoLowLatencyModeCapable',
      params,
      callbackOrValue,
      0,
      null,
    )
  },
  setEdidVersion: function (params) {
    const callbackOrValue = params.value
    delete params.value
    return MockProps.mock(
      'HDMIInput',
      'edidVersion',
      params,
      callbackOrValue,
      0,
      null,
    )
  },
}
